import React, { Component } from 'react';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';
import { Button, Form, FormGroup, FormText, Label, Input } from 'reactstrap';
import Slide from '@material-ui/core/Slide';
import { Container, Row, Col } from 'react-bootstrap';
import { withRouter } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Dropzone from 'react-dropzone'
import './ProjectPrototype.css';


export default class ProjectPrototype extends Component {
    onDrop = (files) => {
        console.log(files);
    }


render() {
    return (
      <div className='concept-container'>
      <h3 className="page-title">Prototypes</h3>
      <Container>
            <div className="text-center mt-5">
                <Dropzone onDrop={this.onDrop} multiple>
                    {({ getRootProps, getInputProps, isDragging}) => (
                        <div {...getRootProps()}>
                            <input {...getInputProps()} />
                                {isDragging ? "Drop your file here" : 'Click or drag a file to upload'}
                            </div>
                    )}
                    </Dropzone>
                    <ul className="list-group mt-2">
                        {acceptedFiles.length > 0 && acceptedFiles.map(acceptedFile => (
                            <li className="list-group-item list-group-item-success">
                                {acceptedFile.name}
                            </li>
                        ))}
                    </ul>
                </div>
            </Container>
            </div>
    );
}
}
