import React, { Component } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.css';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';
import { Button, Form, FormGroup, FormText, Label, Input } from 'reactstrap';
import Slide from '@material-ui/core/Slide';
import { Container } from 'react-bootstrap';
import { withRouter } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Dropzone from 'react-dropzone'
import './ProjectPrototype.css';


export default class ProjectPrototype extends Component {
    constructor(props) {
        super(props);
        this.state = {
            projectName: this.props.location.state.projectName,
            userData: this.props.location.state.userData || this.props.userData,
            prototypeName: '',
            prototypeDescription: '',
        }
    }

    componentDidMount = () => {
        console.log(this.props)
    }

    resetFields = () => {
        this.setState({
            prototypeName: '',
            prototypeDescription: '',
        })
    }

    handlePrototypeNameChange = (event) => {
        this.setState({
            prototypeName: event.target.value
        });
    }
   
    handlePrototypeDescriptionChange = (event) => {
        this.setState({
            prototypeDescription: event.target.value
        });
    }


    onDrop = (files) => {
        console.log(files);
    }


    submitPrototype = () => {
        axios.post('/api/project', {
            headers: {
                Authorization: 'Bearer ' + this.state.userData.token 
            },
            data: {
                'prototypeName': this.state.prototypeName,
                'prototypeDescription': this.state.prototypeDescription
            }
        });
    }


    nextPage = () => {
        this.props.history.push({
            pathname: '/home',
            state: this.state  // need this for moving to different component
        });
    }

    render() {
        return (
          <div className='concept-container'>

                <h3 className="page-title">Prototypes</h3>
                <Container>
                    <div className='prototype-name-holder'>
                        <TextField
                            value={this.state.prototypetName}
                            onChange={this.handlePrototypeNameChange}
                            label="Name"
                            margin="normal"
                            placeholder="Enter Name..."
                            variant="outlined">
                        </TextField>
                    </div>
                    <div className='prototype-description-holder'>
                        <TextField
                            value={this.state.prototypeDescription}
                            onChange={this.handlePrototypeDescriptionChange}
                            label="Description"
                            margin="normal"
                            rows="4"
                            placeholder="Enter Description..."
                            variant="outlined">
                        </TextField>
                    </div>
                    <br></br>
                    <h2 className="page-title">Upload your files</h2>
                    <div className = "Dropzone">
                    <Dropzone onDrop={this.onDrop} multiple>
                        {({ getRootProps, getInputProps, isDragActive, acceptedFiles}) => (
                            <div {...getRootProps()}>
                                <input {...getInputProps()} />
                                    {isDragActive ? "Drop your file here" : 'Click or drag a file to upload'}
                                    <ul className="list-group mt-2">
                                        {acceptedFiles.length > 0 && acceptedFiles.map(acceptedFile => (
                                            <li className="list-group-item list-group-item-success">
                                                {acceptedFile.name}
                                            </li>
                                        ))}
                                    </ul>
                                </div>  
                            )}
                        </Dropzone>
                    </div>              
                </Container>
                </div>
        );
    }
}
